import './App.css';
import { useEffect, useState } from 'react';
import Characters   from './components/Characters';

function App() {

  const [characterList, setCharacterList] = useState([]);

  const [currentPage, setCurrentPage]     = useState('https://rickandmortyapi.com/api/character/');

  useEffect(() => {

    fetch(currentPage)
      .then((response) => response.json())
      .then(response)
      .then((response) => setCurrentPage(response)) 
      .then((response) => setCharacterList(response.results))
      .catch((err)     => console.log(err));

    }, [currentPage]);

    // console.log(characterList);

  return (
    <div className="App">
      <header className="App-header">

        <Characters listCharacters={characterList} />

        {/* <button onClick={() => setCurrentPage(currentPage.info)}>Next Page</button> */}

      </header>
    </div>
  );
}

export default App;
