import './styles.css';

const CharCard = ({name, image, status}) => {

  return (


    <>
      {status === 'Dead' ?

        <div class="flip-container" >
        <div class="flipper" >

        </div>
        </div>


      : 

      <li>
      <span className='card__container'>

        <img src={image} alt="character"  />
        <p>{name}</p>

      </span>
      </li>


      }

    </>
  )
}

export default CharCard;
